```kql
// Create a time range for the past 30 days
let timeRange = now() - 30d;

// Create a table for SigninLogs
let signinLogs = Table.FromTable(
  [
    {"User": "User1", "SigninTime": datetime(2023-01-01T00:00:00), "SignoutTime": datetime(2023-01-01T00:00:00)},
    {"User": "User2", "SigninTime": datetime(2023-01-02T00:00:00), "SignoutTime": datetime(2023-01-02T00:00:00)},
    {"User": "User3", "SigninTime": datetime(2023-01-03T00:00:00), "SignoutTime": datetime(2023-01-03T00:00:00)},
    // Add more signin logs here
  ]
);

// Create a table for SigninLogs with the specified time range
let signinLogsFiltered = signinLogs
| where SigninTime >= timeRange
| where SignoutTime >= timeRange;

// Create a table for SigninLogs with the specified time range and only include signin events
let signinLogsFiltered2 = signinLogsFiltered
| where SigninTime > SignoutTime;

// Create a table for SigninLogs with the specified time range and only include signin events
let signinLogsFiltered3 = signinLogsFiltered
| summarize SigninCount = countif(SigninTime, {SigninTime}) by bin(SigninTime, 1h);

// Create a table for SigninLogs with the specified time range and only include signin events
let signinLogsFiltered4 = signinLogsFiltered3
| where SigninCount > 2;

// Create a time series table for SigninLogs with the specified time range
let signinLogsTS = signinLogsFiltered2
| summarize SigninCount = countif(SigninTime, {SigninTime}) by bin(SigninTime, 1h);

// Create a time series table for SigninLogs with the specified time range and only include signin events
let signinLogsTS2 = signinLogsFiltered3
| summarize SigninCount = countif(SigninTime, {SigninTime}) by bin(SigninTime, 1h);

// Create a time series table for SigninLogs with the specified time range and only include signin events
let signinLogsTS3 = signinLogsFiltered4
| summarize SigninCount = countif(SigninTime, {SigninTime}) by bin(SigninTime, 1h);

