#include <filesystem>

std::vector<AEFont *> FontRandomizer::m_fonts;

void FontRandomizer::initialize()
{
    std::filesystem::path fontsDir = "/path/to/fonts";
    std::filesystem::directory_iterator dir(fontsDir);

    while (dir.hasNext())
    {
        std::filesystem::directory_entry entry = dir.next();
        if (entry.is_regular_file() && entry.path().extension() == ".ttf")
