public class QualityAttribute : Attribute
{
    public string Name { get; set; }
    public string Description { get; set; }
    public string[] Qualities { get; set; }

    public QualityAttribute(string name, string description, string[] qualities)
    {
        Name = name;
        Description = description;
        Qualities = qualities;
    }
}

public class QualityDropdownAttribute : Attribute
{
    public string Name { get; set; }
    public string[] Qualities { get; set; }

    public QualityDropdownAttribute(string name, string[] qualities)
    {
        Name = name;
        Qualities = qualities;
    }
}

public class QualityModel
{
    [Quality("Quality1", "This is quality 1", new string[] { "Yes", "No" })]
    public int Quality1 { get; set; }

    [QualityDropdown("Quality2", new string[] { "High", "Medium", "Low" })]
    public int Quality2 { get; set; }
}
