// app/Http/Middleware/NetworkErrorMiddleware.php

namespace App\Http\Middleware;

use Illuminate\Http\Request;
use Illuminate\Support\Facades\Http;
use Closure;

class NetworkErrorMiddleware
{
    public function handle(Request $request, Closure $next)
    {
        try {
            return $next($request);
        } catch (\Exception $e) {
            if ($e instanceof \Illuminate\Http\Client\HttpResponseException) {
                return response()->json(['error' => 'Network error'], 500);
            } else {
