// Function to print the elements of a 3D array
void printArray(int*** arr, int depth, int width, int height) {
    for (int i = 0; i < depth; i++) {
        for (int j = 0; j < width; j++) {
            for (int k = 0; k < height; k++) {
                std::cout << arr[i][j][k] << " ";
