    // Initialize the array
    for (int i = 0; i < depth; i++) {
        for (int j = 0; j < width; j++) {
            for (int k = 0; k < height; k++) {
                arr[i][j][k] = i + j + k;
            }
        }
    }

    // Print the array
    printArray(arr, depth, width, height);

    // Deallocate memory
    for (int i = 0; i < depth; i++) {
