class FieldManager {
  constructor() {
    // Initialize an empty object to store the fields
    this.fields = {};
  }

  /**
   * Checks if a field is blended
   * @param {string} fieldName - The name of the field to check
   * @returns {boolean} True if the field is blended, false otherwise
   */
  isBlended(fieldName) {
    // Return true if the field exists in the fields object, false otherwise
    return Boolean(this.fields[fieldName]);
  }

  /**
   * Restores a field to its original state
   * @param {string} fieldName - The name of the field to restore
