    }
  }

  /**
   * Blends a field
   * @param {string} fieldName - The name of the field to blend
   * @param {object} options - Options to blend the field (e.g. options.target, options.value)
   */
  blendField(fieldName, options) {
    // Blend the field and store it in the fields object
    this.fields[fieldName] = options;
  }

  /**
   * Unblends a field
   * @param {string} fieldName - The name of the field to unblend
   */
