void createTable(sqlite3* db) {
    char* errorMessage;
    sqlite3_stmt* stmt;
    const char* query = "CREATE TABLE IF NOT EXISTS users (username TEXT PRIMARY KEY, password TEXT, email TEXT)";

    // Prepare the SQL statement
    sqlite3_prepare_v2(db, query, -1, &stmt, 0);

