        self.income[date] = amount
        print(f"Income added: ${amount} on {date} for {category}")

    def add_expense(self, date, amount, category):
        self.expenses[date] = amount
        print(f"Expense added: ${amount} on {date} for {category}")

    def view_income(self):
        print("Income:")
        for date, amount in self.income.items():
            print(f"{date}: ${amount}")

    def view_expenses(self):
        print("Expenses:")
        for date, amount in self.expenses.items():
            print(f"{date}: ${amount}")

    def calculate_balance(self):
        total_income = sum(self.income.values())
        total_expenses = sum(self.expenses.values())
        balance = total_income - total_expenses
        print(f"Balance: ${balance}")


