public class PostingController {
    @Autowired
    private PostingRepository postingRepository;

    @GetMapping("/{period}")
    public List<Posting> getPostings(@PathVariable String period, @RequestParam(required = false) boolean authorized) {
        LocalDate startDate;
        LocalDate endDate = LocalDate.now();
        if (period.equals("day")) {
            startDate = endDate;
        } else if (period.equals("month")) {
            startDate = endDate.withDayOfMonth(1);
        } else if (period.equals("quarter")) {
            startDate = endDate.withMonth(((endDate.getMonthValue() - 1) / 3) * 3 + 1).withDayOfMonth(1);
        } else if (period.equals("year")) {
            startDate = endDate.withDayOfYear(1);
        } else {
            throw new IllegalArgumentException("Invalid period: " + period);
        }

        if (authorized) {
            return postingRepository.findByIsActiveAndAuthorizedDeliveryAndPostDateBetween(true, true, startDate, endDate);
        } else {
            return postingRepository.findByIsActiveAndPostDateBetween(true, startDate, endDate);
        }
    }
}

