const display = document.getElementById('display');
const buttons = document.querySelectorAll('button');
const history = JSON.parse(localStorage.getItem('history')) || [];

let currentNumber = '';
let currentOperation = '';

buttons.forEach(button => {
    button.addEventListener('click', () => {
        if (button.id === 'equals') {
            calculateResult();
        } else if (button.id === 'clear') {
            clearDisplay();
        } else if (button.id === 'delete') {
            deleteLastCharacter();
        } else if (button.id === 'clear') {
            clearDisplay();
        } else if (button.id === '0' || button.id === '1' || button.id === '2' || button.id === '3' || button.id === '4' || button.id === '5' || button.id === '6' || button.id === '7' || button.id === '8' || button.id === '9') {
            appendNumber(button.id);
        } else if (button.id === 'add' || button.id ==='subtract' || button.id ==='multiply' || button.id === 'divide') {
            appendOperation(button.id);
        } else if (button.id === 'decimal') {
            appendDecimal();
        }
    });
});

function appendNumber(number) {
    currentNumber += number;
    display.value = currentNumber;
}

function appendOperation(operation) {
    currentOperation = operation;
    display.value = `${currentNumber} ${operation}`;
}

function appendDecimal() {
    if (!currentNumber.includes('.')) {
        currentNumber += '.';
        display.value = currentNumber;
    }
}

function calculateResult() {
    if (currentOperation === '+') {
        currentNumber = parseFloat(currentNumber) + parseFloat(history[history.length - 1]);
    } else if (currentOperation === '-') {
        currentNumber = parseFloat(currentNumber) - parseFloat(history[history.length - 1]);
    } else if (currentOperation === '*') {
        currentNumber = parseFloat(currentNumber) * parseFloat(history[history.length - 1]);
    } else if (currentOperation === '/') {
        currentNumber = parseFloat(currentNumber) / parseFloat(history[history.length - 1]);
    }

    display.value = currentNumber;
    history.push(currentNumber);
    localStorage.setItem('history', JSON.stringify(history));
    currentNumber = '';
    currentOperation = '';
}

function clearDisplay() {
    display.value = '';
    currentNumber = '';
    currentOperation = '';
}

function deleteLastCharacter() {
    currentNumber = currentNumber.slice(0, -1);
    display.value = currentNumber;
}
```

