#ifndef GAMEBOY_H
#define GAMEBOY_H

#include <stdint.h>

// CPU Registers
typedef struct {
    uint16_t pc; // Program Counter
    uint8_t sp;  // Stack Pointer
    uint8_t a;   // Accumulator
    uint8_t b;   // B Register
    uint8_t c;   // C Register
    uint8_t d;   // D Register
    uint8_t e;   // E Register
    uint8_t h;   // H Register
    uint8_t l;   // L Register
    uint8_t f;   // Flag Register
} gameboy_cpu;

// Memory Layout
typedef struct {
    uint8_t ram[2048]; // 2048 bytes of RAM
    uint8_t rom[0x4000]; // 4KB of ROM
} gameboy_memory;

// Gameboy Emulator
typedef struct {
    gameboy_cpu cpu;
    gameboy_memory memory;
    uint8_t input;
} gameboy_emulator;

// Gameboy Emulator Functions
gameboy_emulator* gameboy_new(void);
void gameboy_reset(gameboy_emulator* emulator);
void gameboy_step(gameboy_emulator* emulator);
void gameboy_draw(gameboy_emulator* emulator);
void gameboy_input(gameboy_emulator* emulator, uint8_t input);

#endif // GAMEBOY_H
