#include "gameboy.h"
#include <stdio.h>
#include <stdint.h>

// Gameboy Emulator Implementation
gameboy_emulator* gameboy_new(void) {
    gameboy_emulator* emulator = malloc(sizeof(gameboy_emulator));
    emulator->cpu.pc = 0;
    emulator->cpu.sp = 0;
    emulator->cpu.a = 0;
    emulator->cpu.b = 0;
    emulator->cpu.c = 0;
    emulator->cpu.d = 0;
    emulator->cpu.e = 0;
    emulator->cpu.h = 0;
    emulator->cpu.l = 0;
    emulator->cpu.f = 0;
    emulator->memory.ram[0x8000] = 0x80; // Initialize stack pointer
    emulator->memory.ram[0x8001] = 0x00; // Initialize stack pointer
    emulator->input = 0;
    return emulator;
}

void gameboy_reset(gameboy_emulator* emulator) {
    emulator->cpu.pc = 0;
    emulator->cpu.sp = 0;
    emulator->cpu.a = 0;
    emulator->cpu.b = 0;
    emulator->cpu.c = 0;
    emulator->cpu.d = 0;
    emulator->cpu.e = 0;
    emulator->cpu.h = 0;
    emulator->cpu.l = 0;
    emulator->cpu.f = 0;
}

void gameboy_step(gameboy_emulator* emulator) {
    uint8_t opcode = (emulator->memory.ram[emulator->cpu.pc]) | ((emulator->memory.ram[emulator->cpu.pc + 1]) << 8);
    uint8_t opcode_byte = opcode & 0xFF;
    uint8_t opcode_shift = opcode >> 8;

    switch (opcode_byte) {
        case 0x00: // NOP
            break;
        case 0x01: // CLD
            emulator->cpu.f |= 0x02; // Set zero flag
            break;
        case 0x02: // CLV
            emulator->cpu.f &= 0xFF; // Clear zero flag
            break;
        case 0x03: // CLD
            emulator->cpu.f |= 0x04; // Set carry flag
            break;
        case 0x04: // CLV
            emulator->cpu.f &= 0xF0; // Clear carry flag
            break;
        case 0x05: // DEC B
            emulator->cpu.b--;
            break;
        case 0x06: // DEC D
            emulator->cpu.d--;
            break;
        case 0x07: // DEC H
            emulator->cpu.h--;
            break;
        case 0x08: // DEC L
            emulator->cpu.l--;
            break;
        case 0x09: // DEC A
            emulator->cpu.a--;
            break;
        case 0x0A: // LD A, B
            emulator->cpu.a = emulator->cpu.b;
            break;
        case 0x0B: // LD A, C
            emulator->cpu.a = emulator->cpu.c;
            break;
        case 0x0C: // LD A, D
            emulator->cpu.a = emulator->cpu.d;
            break;
        case 0x0D: // LD A, E
            emulator->cpu.a = emulator->cpu.e;
            break;
        case 0x0E: // LD A, H
            emulator->cpu.a = emulator->cpu.h;
            break;
        case 0x0F: // LD A, L
            emulator->cpu.a = emulator->cpu.l;
            break;
        case 0x10: // DDN
            break;
        case 0x11: // DDH
            break;
        case 0x12: // DDL
            break;
        case 0x13: // DDM
            break;
        case 0x14: // LD D, A
            emulator->cpu.d = emulator->cpu.a;
            break;
        case 0x15: // LD D, B
            emulator->cpu.d = emulator->cpu.b;
            break;
        case 0x16: // LD D, C
            emulator->cpu.d = emulator->cpu.c;
            break;
        case 0x17: // LD D, E
            emulator->cpu.d = emulator->cpu.e;
            break;
        case 0x18: // LD D, H
            emulator->cpu.d = emulator->cpu.h;
            break;
        case 0x19: // LD D, L
            emulator->cpu.d = emulator->cpu.l;
            break;
        case 0x1A: // LD E, A
            emulator->cpu.e = emulator->cpu.a;
            break;
        case 0x1B: // LD E, B
            emulator->cpu.e = emulator->cpu.b;
            break;
        case 0x1C: // LD E, C
            emulator->cpu.e = emulator->cpu.c;
            break;
        case 0x1D: // LD E, D
            emulator->cpu.e = emulator->cpu.d;
            break;
        case 0x1E: // LD E, H
            emulator->cpu.e = emulator->cpu.h;
            break;
        case 0x1F: // LD E, L
            emulator->cpu.e = emulator->cpu.l;
            break;
        case 0x20: // LD H, A
            emulator->cpu.h = emulator->cpu.a;
            break;
        case 0x21: // LD H, B
            emulator->cpu.h = emulator->cpu.b;
