public class FireplaceAutoMoveMod {

    public FireplaceAutoMoveMod() {
        // Register the mod's entity
        EntityRegistry.registerEntity(FireplaceAutoMoveEntity.class, "fireplaceautomove_entity");
    }

    public static class FireplaceAutoMoveEntity extends LivingEntity {
        public FireplaceAutoMoveEntity(EntityType<?> type, Level world) {
            super(type, world);
        }

        @Override
        public void tick() {
            super.tick();

            // Check if the player has a fireplace in their inventory
            if (hasFireplaceInInventory()) {
                // Move the entity to the nearest green glass block
                moveToNearestGreenGlassBlock();
            }
        }

        private boolean hasFireplaceInInventory() {
            // Check if the player has a fireplace in their inventory
            // This is a very basic implementation, you may need to adjust it to your needs
            ItemStack fireplaceStack = getItemStackInInventory("fireplace");
            return fireplaceStack!= null && fireplaceStack.getItem() == FireplaceItem.get();
        }

        private void moveToNearestGreenGlassBlock() {
            // Get the player's position
            BlockPos playerPos = getBlockPos();

            // Find the nearest green glass block
            BlockPos nearestGlassPos = findNearestGreenGlassBlock(playerPos);

            // If a green glass block is found, move the entity to it
            if (nearestGlassPos!= null) {
                moveBlockPos(nearestGlassPos);
            }
        }

        private BlockPos findNearestGreenGlassBlock(BlockPos playerPos) {
            // Get the player's level
            Level level = playerPos.getWorld();

            // Find the nearest green glass block
            BlockPos nearestGlassPos = null;
            double minDistance = Double.MAX_VALUE;

            for (int x = -1; x <= 1; x++) {
                for (int y = -1; y <= 1; y++) {
                    for (int z = -1; z <= 1; z++) {
                        BlockPos glassPos = playerPos.add(x, y, z);

                        // Check if the block is green glass
                        BlockState glassState = level.getBlockState(glassPos);
                        if (glassState.getBlock() == GlassBlock.class) {
                            double distance = glassPos.distance(playerPos);

                            // Update the nearest green glass block
                            if (distance < minDistance) {
                                minDistance = distance;
                                nearestGlassPos = glassPos;
                            }
                        }
                    }
                }
            }

            return nearestGlassPos;
        }

        private void moveBlockPos(BlockPos pos) {
            // Get the player's level
            Level level = getLevel();

            // Move the player to the block position
            level.setEntityPosition(this, pos);
        }
    }
}
