class Shape:
    def __init__(self):
        self.x = 5
        self.y = 0
        self.color = (random.randint(0, 255), random.randint(0, 255), random.randint(0, 255))
        self.blocks = []

    def rotate(self):
        self.blocks = [
            [-1, 0],
            [0, -1],
            [1, 0],
            [0, 1]
        ]

    def get_blocks(self):
