class IShape(Shape):
    def __init__(self):
        super().__init__()
        self.blocks = [
            [0, 0],
            [0, 0],
            [0, 0],
            [0, 0]
        ]

class JShape(Shape):
    def __init__(self):
        super().__init__()
        self.blocks = [
            [0, 0],
            [1, 0],
            [1, 1],
            [0, 1]
        ]

class LShape(Shape):
    def __init__(self):
        super().__init__()
        self.blocks = [
            [0, 0],
            [1, 0],
            [1, 1],
            [0, 1]
        ]

class OShape(Shape):
    def __init__(self):
        super().__init__()
        self.blocks = [
            [0, 0],
            [0, 0],
            [1, 0],
            [1, 0]
        ]

class SShape(Shape):
    def __init__(self):
        super().__init__()
        self.blocks = [
            [0, 0],
            [1, 0],
            [1, 1],
            [0, 1]
        ]

class TShape(Shape):
    def __init__(self):
        super().__init__()
        self.blocks = [
            [0, 0],
            [1, 0],
            [0, 1],
            [1, 1]
        ]

class ZShape(Shape):
    def __init__(self):
        super().__init__()
        self.blocks = [
            [0, 0],
            [1, 0],
            [0, 1],
            [1, 1]
        ]

class Grid:
    def __init__(self):
        self.width = 10
        self.height = 20
        self.grid = [[0 for _ in range(self.width)] for _ in range(self.height)]

    def is_valid(self, shape):
        for block in shape.blocks:
            if block[0] + shape.x < 0 or block[0] + shape.x >= self.width:
                return False
            if block[1] + shape.y < 0 or block[1] + shape.y >= self.height:
                return False
            if self.grid[block[1] + shape.y][block[0] + shape.x] == 1:
                return False
        return True

    def add_shape(self, shape):
        for block in shape.blocks:
            self.grid[block[1] + shape.y][block[0] + shape.x] = 1

    def clear_lines(self):
        lines = 0
        for i in range(self.height):
            if all(cell == 1 for cell in self.grid[i]):
                self.grid.pop(i)
                self.grid.insert(0, [0 for _ in range(self.width)])
                lines += 1
        return lines

def draw_grid(grid, shape):
    screen.fill(WHITE)
    for i in range(grid.height):
        for j in range(grid.width):
            pygame.draw.rect(screen, (0, 0, 0), (j * 50, i * 50, 50, 50), 1)
    for i in range(shape.y, shape.y + len(shape.blocks)):
        for j in range(shape.x, shape.x + len(shape.blocks)):
            if grid.grid[i][j] == 1:
                pygame.draw.rect(screen, shape.color, (j * 50 + 5, i * 50 + 5, 40, 40))

def main():
    pygame.init()
    screen = pygame.display.set_mode((WIDTH, HEIGHT))
    clock = pygame.time.Clock()
    grid = Grid()
    shapes = [IShape(), JShape(), LShape(), OShape(), SShape(), TShape(), ZShape()]
    shape = shapes[random.randint(0, len(shapes) - 1)]
    running = True
    while running:
        for event in pygame.event.get():
            if event.type == pygame.QUIT:
                running = False
            if event.type == pygame.KEYDOWN:
                if event.key == pygame.K_LEFT:
                    if grid.is_valid(shape):
                        shape.x -= 1
                if event.key == pygame.K_RIGHT:
                    if grid.is_valid(shape):
                        shape.x += 1
                if event.key == pygame.K_DOWN:
                    if grid.is_valid(shape):
                        shape.y += 1
                if event.key == pygame.K_UP:
                    shape.rotate()
                    if not grid.is_valid(shape):
                        shape.rotate()
                        shape.rotate()
                        shape.rotate()
        if not grid.is_valid(shape):
            grid.add_shape(shape)
            shape = shapes[random.randint(0, len(shapes) - 1)]
            if grid.clear_lines() > 0:
                print("Lines cleared:", grid.clear_lines())
        draw_grid(grid, shape)
        pygame.display.flip()
        clock.tick(60)
    pygame.quit()

if __name__ == "__main__":
    main()
```

This code creates a window with a grid where shapes can be dropped from the top. The shapes can be moved left, right, down, and rotated using the corresponding keys. The game ends when the window is closed.

