        // Calculate the mileage rate after percentage increase
        double mileageRate = initialMileageRate + (initialMileageRate * (percentageIncrease / 100));

        // Calculate the mileage rate for the k day of training
        double mileageRateK = mileageRate * Math.Pow((1 + (percentageIncrease / 100)), (day - 1));

