        Console.WriteLine("\nTask 2: Calculate the number of years it takes for the population to double");
        Console.WriteLine("Enter the initial population (A):");
        double A = Convert.ToDouble(Console.ReadLine());
        Console.WriteLine("Enter the annual increase rate (B)%:");
        double B = Convert.ToDouble(Console.ReadLine()) / 100;

