    static double[] GetTable(double a, double b, double h)
    {
        double[] table = new double[(int)(b - a) / h + 1];
        double currentValue = a;
        for (int i = 0; i < table.Length; i++)
        {
            table[i] = currentValue * currentValue * currentValue - 2 * currentValue + 8;
            currentValue += h;
        }
        return table;
    }

    static void PrintTable(double[] table)
    {
        Console.WriteLine("Table of values:");
        for (int i = 0; i < table.Length; i++)
        {
            Console.WriteLine($"{i * 1.0}  {table[i]}");
        }
    }

    static double GetYears(double A, double B)
    {
        double years = 0;
        double population = A;
        while (population < A * 2)
        {
            population *= (1 + B / 100);
            years++;
        }
        return years;
    }

